/**
 * Giftvoucher Typeofbarcode Model
 *
 * @category Simi
 * @package  Simi_Simigiftvoucher
 * @module   Giftvoucher
 * @author   Simi Developer
 */

class Simi_Simigiftvoucher_Model_Typeofbarcode
{

    /**
     * Get model option as array
     *
     * @return array
     */
    public function toOptionArray()
    {
        $positions = array(
            'code128' => Mage::helper('simigiftvoucher')->__('Code 128'),
            'qr' => Mage::helper('simigiftvoucher')->__('QR code'),
        );
        $options = array();
        foreach ($positions as $code => $label) {
            $options[] = array(
                'value' => $code,
                'label' => $label
            );
        }
        return $options;
    }

}
